// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./IArbitrator.sol";

/**
/** @title IArbitrable
 *  @author Enrique Piqueras - <enrique@kleros.io>
 *  Arbitrable interface.
 *  When developing arbitrable contracts, we need to:
 *  -Define the action taken when a ruling is received by the contract. We should do so in executeRuling.
 *  -Allow dispute creation. For this a function must:
 *      -Call arbitrator.createDispute.value(_fee)(_choices,_extraData);
 *      -Create the event Dispute(_arbitrator,_disputeID,_rulingOptions);
 */
interface IArbitrable {
    /** @dev To be emmited when meta-evidence is submitted.
     *  @param _metaEvidenceID Unique identifier of meta-evidence.
     *  @param _evidence A link to the meta-evidence JSON.
     */
    event MetaEvidence(uint indexed _metaEvidenceID, string _evidence);

    /** @dev To be emmited when a dispute is created to link the correct meta-evidence to the disputeID
     *  @param _arbitrator The arbitrator of the contract.
     *  @param _disputeID ID of the dispute in the IArbitrator contract.
     *  @param _metaEvidenceID Unique identifier of meta-evidence.
     *  @param _evidenceGroupID Unique identifier of the evidence group that is linked to this dispute.
     */
    event Dispute(
        IArbitrator indexed _arbitrator,
        uint indexed _disputeID,
        uint _metaEvidenceID,
        uint _evidenceGroupID
    );

    /** @dev To be raised when evidence are submitted. Should point to the ressource (evidences are not to
     *       be stored on chain due to gas considerations).
     *  @param _arbitrator The arbitrator of the contract.
     *  @param _evidenceGroupID Unique identifier of the evidence group the evidence belongs to.
     *  @param _party The address of the party submiting the evidence. Note that 0x0 refers to evidence
     *         not submitted by any party.
     *  @param _evidence A URI to the evidence JSON file whose name should be its keccak256 hash followed by .json.
     */
    event Evidence(
        IArbitrator indexed _arbitrator,
        uint indexed _evidenceGroupID,
        address indexed _party,
        string _evidence
    );

    /** @dev To be raised when a ruling is given.
     *  @param _arbitrator The arbitrator giving the ruling.
     *  @param _disputeID ID of the dispute in the IArbitrator contract.
     *  @param _ruling The ruling which was given.
     */
    event Ruling(IArbitrator indexed _arbitrator, uint indexed _disputeID, uint _ruling);

    /** @dev Give a ruling for a dispute. Must be called by the arbitrator.
     *  The purpose of this function is to ensure that the address calling it has the right to rule on the contract.
     *  @param _disputeID ID of the dispute in the IArbitrator contract.
     *  @param _ruling Ruling given by the arbitrator. Note that 0 is reserved for
     *         "Not able/wanting to make a decision".
     */
    function rule(uint _disputeID, uint _ruling) external;
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {IArbitrable} from "./IArbitrable.sol";
import {IDAO} from "@aragon/osx/core/dao/IDAO.sol";

interface IArbitrableProposal is IArbitrable {
    ///--------------------------------------------------------------------------///
    ///                                EVENTS                                    ///
    ///--------------------------------------------------------------------------///

    /// @dev Emitted when a configuration parameter is updated.
    /// @param what The name of the parameter that was updated.
    /// @param value The new value of the parameter.
    event UpdateParameters(bytes32 what, bytes value);

    /// @dev Emitted when a new proposal is created.
    /// @param proposalId The ID of the newly created proposal.
    /// @param proposer The address of the proposer.
    /// @param metadata The metadata associated with the proposal.
    event ProposalCreated(uint256 proposalId, address proposer, bytes metadata);

    /// @dev Emitted when a party has to pay a fee.
    /// @param party The address of the party.
    event HasToPayFee(address party);

    /// @dev Emitted when a proposal is disputed.
    /// @param proposalId The ID of the disputed proposal.
    /// @param disputeId The ID of the dispute created in the arbitrator.
    /// @param challenger The address of the challenger.
    event ProposalDisputed(uint256 proposalId, uint256 disputeId, address challenger);

    /// @dev Emitted when a proposal is canceled.
    /// @param proposalId The ID of the canceled proposal.
    event ProposalCanceled(uint256 proposalId);

    /// @notice CollateralWithdrawn
    /// @dev Emitted when a proposer withdraws their collateral.
    /// @param proposer The address of the proposer.
    /// @param amount The amount of collateral withdrawn.
    event CollateralWithdrawn(address indexed proposer, uint256 amount);

    /// @notice CollateralDeposited
    /// @dev Emitted when a proposer deposits collateral.
    /// @param proposer The address of the proposer.
    /// @param amount The amount of collateral deposited.
    event CollateralDeposited(address indexed proposer, uint256 amount);

    event ProposalRuled(uint256 proposalId, uint256 disputeID, uint ruling);
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./IArbitrable.sol";

import "./IArbitrable.sol";

/**
 * @title Arbitrator
 * Arbitrator abstract contract.
 * When developing arbitrator contracts we need to:
 * - Define the functions for dispute creation (createDispute) and appeal (appeal). Don't forget to
 * - store the arbitrated contract and the disputeID (which should be unique, may nbDisputes).
 * - Define the functions for cost display (arbitrationCost and appealCost).
 * - Allow giving rulings. For this a function must call arbitrable.rule(disputeID, ruling).
 */
interface IArbitrator {
    enum DisputeStatus {
        Waiting,
        Appealable,
        Solved
    }

    /**
     * @dev To be emitted when a dispute is created.
     * @param _disputeID ID of the dispute.
     * @param _arbitrable The contract which created the dispute.
     */
    event DisputeCreation(uint256 indexed _disputeID, IArbitrable indexed _arbitrable);

    /**
     * @dev To be emitted when a dispute can be appealed.
     * @param _disputeID ID of the dispute.
     * @param _arbitrable The contract which created the dispute.
     */
    event AppealPossible(uint256 indexed _disputeID, IArbitrable indexed _arbitrable);

    /**
     * @dev To be emitted when the current ruling is appealed.
     * @param _disputeID ID of the dispute.
     * @param _arbitrable The contract which created the dispute.
     */
    event AppealDecision(uint256 indexed _disputeID, IArbitrable indexed _arbitrable);

    /**
     * @dev Create a dispute. Must be called by the arbitrable contract.
     * Must be paid at least arbitrationCost(_extraData).
     * @param _choices Amount of choices the arbitrator can make in this dispute.
     * @param _extraData Can be used to give additional info on the dispute to be created.
     * @return disputeID ID of the dispute created.
     */
    function createDispute(
        uint256 _choices,
        bytes calldata _extraData
    ) external payable returns (uint256 disputeID);

    /**
     * @dev Compute the cost of arbitration. It is recommended not to increase it often,
     *      as it can be highly time and gas consuming for the arbitrated contracts to
     *      cope with fee augmentation.
     * @param _extraData Can be used to give additional info on the dispute to be created.
     * @return cost Amount to be paid.
     */
    function arbitrationCost(bytes calldata _extraData) external view returns (uint256 cost);

    /**
     * @dev Appeal a ruling. Note that it has to be called before the arbitrator contract calls rule.
     * @param _disputeID ID of the dispute to be appealed.
     * @param _extraData Can be used to give extra info on the appeal.
     */
    function appeal(uint256 _disputeID, bytes calldata _extraData) external payable;

    /**
     * @dev Compute the cost of appeal. It is recommended not to increase it often, as it can
     *      be higly time and gas consuming for the arbitrated contracts to cope with fee augmentation.
     * @param _disputeID ID of the dispute to be appealed.
     * @param _extraData Can be used to give additional info on the dispute to be created.
     * @return cost Amount to be paid.
     */
    function appealCost(
        uint256 _disputeID,
        bytes calldata _extraData
    ) external view returns (uint256 cost);

    /**
     * @dev Compute the start and end of the dispute's current or next appeal period, if possible.
     *      If not known or appeal is impossible: should return (0, 0).
     * @param _disputeID ID of the dispute.
     * @return start The start of the period.
     * @return end The end of the period.
     */
    function appealPeriod(uint256 _disputeID) external view returns (uint256 start, uint256 end);

    /**
     * @dev Return the status of a dispute.
     * @param _disputeID ID of the dispute to rule.
     * @return status The status of the dispute.
     */
    function disputeStatus(uint256 _disputeID) external view returns (DisputeStatus status);

    /**
     * @dev Return the current ruling of a dispute. This is useful for parties to know if they should appeal.
     * @param _disputeID ID of the dispute.
     * @return ruling The ruling which has been given or the one which will be given if there is no appeal.
     */
    function currentRuling(uint256 _disputeID) external view returns (uint256 ruling);
}

