// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {IDAO, PluginUUPSUpgradeable} from "@aragon/osx/core/plugin/PluginUUPSUpgradeable.sol";
import {IMembership} from "@aragon/osx/core/plugin/membership/IMembership.sol";

import {IArbitrator} from "./interfaces/IArbitrator.sol";
import {Proposals} from "./base/Proposals.sol";
import {ArbitrableProposal} from "./base/ArbitrableProposal.sol";

/// @title OptimisticProposalPlugin
/// @author Aaron Abu Usama (@pythonpete32)
/// @notice A plugin that manages and executes Optimistic Proposals for an Aragon OSx DAO.
contract OptimisticProposalPlugin is ArbitrableProposal, PluginUUPSUpgradeable {
    /// @notice The ID of the permission required to call the `rule` function.
    bytes32 public constant RULE_PERMISSION_ID = keccak256("RULE_PERMISSION");

    /// @notice The ID of the permission required to call the `executeProposal` function.
    bytes32 public constant CREATE_PROPOSAL_PERMISSION_ID = keccak256("CREATE_PROPOSAL_PERMISSION");

    /// @notice The ID of the permission required to call the `file` function.
    bytes32 public constant CONFIGURE_PARAMETERS_PERMISSION_ID =
        keccak256("CONFIGURE_PARAMETERS_PERMISSION_");

    /// @notice Initializes the component.
    /// @dev This method is required to support [ERC-1822](https://eips.ethereum.org/EIPS/eip-1822).
    /// @param _dao The IDAO interface of the associated DAO.
    /// @param _arbitrator the Arbitrator contract
    function initialize(
        IDAO _dao,
        IArbitrator _arbitrator,
        uint256 _executionDelay,
        uint256 _proposalCollateral,
        string calldata _metaEvidence,
        bytes calldata _arbitratorExtraData
    ) external initializer {
        __PluginUUPSUpgradeable_init(_dao);
        __ArbitrableProposal_init(
            _executionDelay,
            _arbitrator,
            _proposalCollateral,
            _metaEvidence,
            _arbitratorExtraData
        );
    }

    function updateState(
        bytes32 what,
        bytes calldata value
    ) external auth(CONFIGURE_PARAMETERS_PERMISSION_ID) {
        _updateState(what, value);
    }

    function createProposal(
        bytes calldata _metadata,
        IDAO.Action[] calldata _actions,
        uint256 _allowFailureMap
    ) external payable auth(CREATE_PROPOSAL_PERMISSION_ID) {
        _createArbitrableProposal(_metadata, _actions, _allowFailureMap);
    }

    function rule(uint256 _disputeID, uint256 _ruling) external auth(RULE_PERMISSION_ID) {
        _rule(_disputeID, _ruling);
    }

    ///--------------------------------------------------------------------------///
    ///                                 IMembership                              ///
    ///--------------------------------------------------------------------------///

    function isMember(address _account) external view returns (bool) {
        return
            dao().hasPermission({
                _where: address(this),
                _who: _account,
                _permissionId: CREATE_PROPOSAL_PERMISSION_ID,
                _data: bytes("")
            });
    }

    ///--------------------------------------------------------------------------///
    ///                                 EIP-165                                  ///
    ///--------------------------------------------------------------------------///

    /// @notice The interface ID of the contract.
    bytes4 internal constant DISPUTABLE_PLUGIN_INTERFACE_ID =
        this.initialize.selector ^ this.rule.selector;

    /// @notice Checks if this or the parent contract supports an interface by its ID.
    /// @param _interfaceId The ID of the interface.
    /// @return Returns `true` if the interface is supported.
    function supportsInterface(
        bytes4 _interfaceId
    ) public view override(PluginUUPSUpgradeable, Proposals) returns (bool) {
        return
            _interfaceId == DISPUTABLE_PLUGIN_INTERFACE_ID ||
            _interfaceId == type(IMembership).interfaceId ||
            super.supportsInterface(_interfaceId);
    }

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[49] private __gap;
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {PluginSetup, IPluginSetup} from "@aragon/osx/framework/plugin/setup/PluginSetup.sol";

import {IArbitrator} from "./interfaces/IArbitrator.sol";
import {IDAO} from "@aragon/osx/core/dao/IDAO.sol";
import {DAO} from "@aragon/osx/core/dao/DAO.sol";
import {PermissionLib} from "@aragon/osx/core/permission/PermissionLib.sol";
import {OptimisticProposalPlugin} from "./OptimisticProposalPlugin.sol";

/// @title TemplatePluginSetup
/// @author DAO BOX
/// @notice The setup contract of the `OptimisticProposalPlugin` contract.
contract OptimisticProposalSetup is PluginSetup {
    /// @notice The address of the `OptimisticProposalPlugin` base contract.
    OptimisticProposalPlugin private immutable optimisticProposals;

    /// @notice The error thrown when the helpers array length is not x.
    error WrongHelpersArrayLength(uint length);

    /// @notice The contract constructor, that deployes the bases.
    constructor() {
        optimisticProposals = new OptimisticProposalPlugin();
    }

    /// @inheritdoc IPluginSetup
    function prepareInstallation(
        address _dao,
        bytes calldata _data
    ) external returns (address plugin, PreparedSetupData memory preparedSetupData) {
        // Decode `_data` to extract the params needed for deploying and initializing `OptimisticProposalPlugin` plugin,
        // and the required helpers
        (
            IArbitrator arbitrator,
            uint256 executionDelay,
            uint256 proposalCollateral,
            string memory metaEvidence,
            bytes memory arbitratorExtraData
        ) = abi.decode(_data, (IArbitrator, uint256, uint256, string, bytes));

        // Prepare and deploy plugin proxy.
        plugin = createERC1967Proxy(
            address(optimisticProposals),
            abi.encodeWithSelector(
                OptimisticProposalPlugin.initialize.selector,
                _dao,
                arbitrator,
                executionDelay,
                proposalCollateral,
                metaEvidence,
                arbitratorExtraData
            )
        );

        // Prepare permissions
        PermissionLib.MultiTargetPermission[]
            memory permissions = new PermissionLib.MultiTargetPermission[](4);

        // Revoke the Arbitrator contract `RULE_PERMISSION_ID` of the plugin.
        permissions[0] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            plugin,
            address(arbitrator),
            PermissionLib.NO_CONDITION,
            optimisticProposals.RULE_PERMISSION_ID()
        );

        // Revoke the DAO contract `CREATE_PROPOSAL_PERMISSION_ID` of the plugin.
        permissions[1] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            plugin,
            address(_dao),
            PermissionLib.NO_CONDITION,
            optimisticProposals.CREATE_PROPOSAL_PERMISSION_ID()
        );

        // Revoke the DAO contract `CONFIGURE_PARAMETERS_PERMISSION_ID` of the plugin.
        permissions[2] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            plugin,
            address(_dao),
            PermissionLib.NO_CONDITION,
            optimisticProposals.CONFIGURE_PARAMETERS_PERMISSION_ID()
        );

        // Revoke `EXECUTE_PERMISSION` of the DAO to the plugin.
        permissions[3] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            _dao,
            plugin,
            PermissionLib.NO_CONDITION,
            DAO(payable(_dao)).EXECUTE_PERMISSION_ID()
        );

        preparedSetupData.permissions = permissions;
    }

    /// @inheritdoc IPluginSetup
    function prepareUninstallation(
        address _dao,
        SetupPayload calldata _payload
    ) external view returns (PermissionLib.MultiTargetPermission[] memory permissions) {
        // Prepare permissions.

        permissions = new PermissionLib.MultiTargetPermission[](3);

        // Grant the Arbitrator contract `RULE_PERMISSION_ID` of the plugin.
        // permissions[0] = PermissionLib.MultiTargetPermission(
        //   PermissionLib.Operation.Grant,
        //   _payload.plugin,
        //   arbitrator,
        //   PermissionLib.NO_CONDITION,
        //   optimisticProposals.RULE_PERMISSION_ID()
        // );

        // Grant the DAO contract `CREATE_PROPOSAL_PERMISSION_ID` of the plugin.
        permissions[0] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            _payload.plugin,
            address(_dao),
            PermissionLib.NO_CONDITION,
            optimisticProposals.CREATE_PROPOSAL_PERMISSION_ID()
        );

        // Grant the DAO contract `CONFIGURE_PARAMETERS_PERMISSION_ID` of the plugin.
        permissions[1] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            _payload.plugin,
            address(_dao),
            PermissionLib.NO_CONDITION,
            optimisticProposals.CONFIGURE_PARAMETERS_PERMISSION_ID()
        );

        // Grant `EXECUTE_PERMISSION` of the DAO to the plugin.
        permissions[2] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            _dao,
            _payload.plugin,
            PermissionLib.NO_CONDITION,
            DAO(payable(_dao)).EXECUTE_PERMISSION_ID()
        );
    }

    /// @inheritdoc IPluginSetup
    function implementation() external view virtual override returns (address) {
        return address(optimisticProposals);
    }
}

